mapper.select:
select GROUP_NAME
from GROUP_INFO
where GROUP_ID = #{groupId,jdbcType=INTEGER}

select DEP_NAME
from DEP_INFO
where DEP_ID = #{depId,jdbcType=INTEGER}

select COMPANY_NAME
from COMPANY_INFO
where COMPANY_ID = #{companyId,jdbcType=INTEGER}

select USER_NAME
from USER_INFO
where USER_ID = #{userId,jdbcType=INTEGER}

mapper.update:
update USER_INFO
set PASSWORD = #{password}
where EMAIL = #{email}

mapper.select:
select EXT_1,EXT_2,EXT_3
from USER_INFO
where EMAIL = #{email}

select u1.USER_NAME, u1.GENDER, u1.ROOM_NUM, u1.PHONE_NUM, u1.EMAIL,u1.GROUP_ID, u1.COMPANY_ID, u1.DEP_ID, u1.ROLE_ID, u1.STATUS, u1.LINE_LEADER, u1.UPDATE_TIME, u1.CREATE_TIME,u1.EXT_1,u1.EXT_2,u1.EXT_3, r.ROLE_NAME, g.GROUP_NAME, d.DEP_NAME, c1.COMPANY_NAME, u2.USER_NAME AS LEADER_NAME from USER_INFO u1, ROLE_INFO r, GROUP_INFO g, DEP_INFO d, COMPANY_INFO c1, USER_INFO u2
u1.ROLE_ID = r.ROLE_ID and g.GROUP_ID = u1.GROUP_ID and d.DEP_ID = u1.DEP_ID and c1.COMPANY_ID = u1.COMPANY_ID and u1.LINE_LEADER = u2.USER_ID and
u1.GROUP_ID = #{grpId}
u1.DEP_ID = #{depId}
u1.COMPANY_ID = #{comId}

select u1.USER_NAME, u1.GENDER,
u1.ROOM_NUM, u1.PHONE_NUM, u1.EMAIL,u1.GROUP_ID,
u1.COMPANY_ID, u1.DEP_ID, u1.ROLE_ID,
u1.STATUS, u1.LINE_LEADER, u1.UPDATE_TIME,
u1.CREATE_TIME,u1.EXT_1,u1.EXT_2,u1.EXT_3, r.ROLE_NAME, g.GROUP_NAME, d.DEP_NAME, c1.COMPANY_NAME, u2.USER_NAME AS LEADER_NAME
from USER_INFO u1, ROLE_INFO r, GROUP_INFO g, DEP_INFO d, COMPANY_INFO c1, USER_INFO u2

  u1.ROLE_ID = r.ROLE_ID
  and
  g.GROUP_ID = u1.GROUP_ID
  and
  d.DEP_ID = u1.DEP_ID
  and
  c1.COMPANY_ID = u1.COMPANY_ID
  and
  u1.LINE_LEADER = u2.USER_ID
  and


    u1.USER_NAME = #{username}


    u1.GENDER = #{gender}


    u1.ROOM_NUM = #{roomNum}


    u1.PHONE_NUM = #{phone}


    u1.EMAIL = #{email}


    u1.GROUP_ID= #{grpId}


    u1.DEP_ID = #{depId}


    u1.COMPANY_ID = #{comId}


    u1.ROLE_ID = #{roleId}


    u1.STATUS = #{isDelete}

mapper.update:
update USER_INFO
set USER_NAME=#{username}, GENDER=#{gender}, ROOM_NUM=#{roomNum},DEP_ID=#{depId},COMPANY_ID=#{comId},GROUP_ID=#{grpId},ROLE_ID=#{roleId},STATUS=#{status},
PHONE_NUM=#{phoneNum},
EMAIL=#{newEmail}
where EMAIL=#{oldEmail}

update USER_INFO
set LINE_LEADER=#{lineLeader}
where EMAIL =#{email}
